import React, { Component } from "react";
import Slider from "react-slick";


export default class SimpleSlider extends Component {
    render() {
        const settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1
        };
        return (
            <div>
                <h2> Single Item</h2>
                <Slider {...settings}>
                    <div>
                        <img src="./../src/assets/hinh1.jpg" />
                    </div>
                    <div>
                        <img src="../../assets/hinh2.jpg" alt="slider" />
                    </div>
                    <div>
                        <img src="../../assets/hinh3.jpg" alt="slider" />
                    </div>
                    <div>
                        <img src="../../assets/hinh4.jpg" alt="slider" />
                    </div>
                    <div>
                        <img src="../../assets/hinh5.jpg" alt="slider" />
                    </div>

                </Slider>
            </div>
        );
    }
}
