import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getTheaterInfoApi, getTheaterListApi } from '../../redux/actions/GetMovieAction';

export default function MovieTable() {

    let [state, setState] = useState({
        numb: 0
    })

    const { theaterList, theaterInfo } = useSelector(state => state.GetMovieReducer);

    const dispatch = useDispatch();

    const getTheaterList = () => {
        dispatch(getTheaterListApi())
    }

    const getTheaterInfo = (maHeThongRap) => {
        dispatch(getTheaterInfoApi(maHeThongRap))
    }

    const getTheaterShowtime = () => {
        dispatch(getTheaterShowtimeApi)
    }

    useEffect(() => {
        getTheaterList();
        return () => {

        }
    }, [])

    const renderLogo = (index) => {
        return setState({
            ...state,
            numb: index
        })
    };


    const renderTheater = () => {
        return theaterList.map((rap, index) => {
            return <div key={index}>
                <div >
                    <img
                        onClick={() => {
                            getTheaterInfo(rap.maHeThongRap)
                            renderLogo(index)
                            console.log(index)
                        }}
                        style={{ cursor: 'pointer', height: "80px", width: '80px', padding: '10px' }}
                        src={rap.logo} />
                </div>
            </div>
        })
    }

    const renderTheaterInfo = () => {
        return theaterInfo.map((item, index) => {
            return <div key={index}>
                <img
                    src={theaterList[state.numb].logo}
                    style={{ cursor: 'pointer', height: "80px", width: '80px', padding: '10px' }}
                />
                <h4>{item.tenCumRap}</h4>
                <p>{item.diaChi}</p>
            </div>
        })
    }

    return (
        <div className="container d-flex">
            <div>
                {renderTheater()}
            </div>

            <div>
                {renderTheaterInfo()}
            </div>

            <div>

            </div>
        </div>
    )
}
