import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getMovieListApi } from '../../redux/actions/GetMovieAction';
import './MovieList.css';
// Import css files
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from 'react-slick';

export default function MovieList(props) {
    var settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        slidesPerRow: 4
    };

    const { movieList } = useSelector(state => state.GetMovieReducer);
    const dispatch = useDispatch();

    let [state, setState] = useState({
        movie: [{
            maPhim: 1314,
            tenPhim: "Ted 2",
            biDanh: "ted-2",
            trailer: "https://www.youtube.com/embed/S3AVcCggRnU",
            hinhAnh: "http://movie0706.cybersoft.edu.vn/hinhanh/ted2.jpg",
            moTa: "Newlywed couple Ted and Tami-Lynn want to have a baby, but in order to qualify to be a parent, Ted will have to prove he's a person in a court of law.",
            maNhom: "GP01",
            ngayKhoiChieu: "2019-07-29T00:00:00",
            danhGia: 5
        },


        ]

    })

    const getMovieList = () => {
        dispatch(getMovieListApi())
    }

    useEffect(() => {
        getMovieList();
        return () => {

        }
    }, [])

    const renderMovieList = () => {
        return movieList.map((phim, index) => {
            return <div key={index} className=''>
                <div>
                    <div className="card text-black ">
                        <img className="card-img-top" src={phim.hinhAnh} alt />
                        <div className="card-body">
                            <p className="card-text">{phim.tenPhim}</p>
                        </div>
                    </div>
                </div>
            </div>
        })
    }


    return (
        <div className="container">
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <div className="title">
                    <h4>Đang chiếu</h4>
                </div>
                <div className="title">
                    <h4>Sắp chiếu</h4>
                </div>
            </div>

            <div>
                <div className="example">


                    <Slider className="container" {...settings}>
                        <div className="row">
                            <div className=''>
                                {renderMovieList()}
                            </div>
                        </div>

                    </Slider>


                </div>
            </div>



        </div>
    )
}
