import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getMovieListApi } from '../../redux/actions/GetMovieAction';
import './MovieList.css';

export default function MovieList(props) {

    const { movieList } = useSelector(state => state.GetMovieReducer);
    const dispatch = useDispatch();

    let [state, setState] = useState({
        movie: [{
            maPhim: 1314,
            tenPhim: "Ted 2",
            biDanh: "ted-2",
            trailer: "https://www.youtube.com/embed/S3AVcCggRnU",
            hinhAnh: "http://movie0706.cybersoft.edu.vn/hinhanh/ted2.jpg",
            moTa: "Newlywed couple Ted and Tami-Lynn want to have a baby, but in order to qualify to be a parent, Ted will have to prove he's a person in a court of law.",
            maNhom: "GP01",
            ngayKhoiChieu: "2019-07-29T00:00:00",
            danhGia: 5
        },


        ]

    })

    const getMovieList = () => {
        dispatch(getMovieListApi())
    }

    useEffect(() => {
        getMovieList();
        return () => {

        }
    }, [])

    const renderMovieList = () => {
        return movieList.map((phim, index) => {
            return <div key={index} className='col-3'>
                <div>
                    <div className="card text-black ">
                        <img className="card-img-top" src={phim.hinhAnh} alt />
                        <div className="card-body">
                            <p className="card-text">{phim.tenPhim}</p>
                        </div>
                    </div>
                </div>
            </div>
        })
    }


    return (
        <div className="container">
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <div className="title">
                    <h4>Đang chiếu</h4>
                </div>
                <div className="title">
                    <h4>Sắp chiếu</h4>
                </div>
            </div>
            <div>
                <div className="example">
                    <div className='row'>

                        {/* {renderMovieList()} */}

                    </div>
                </div>
            </div>
            <div id="carouselId" className="carousel slide" data-ride="carousel">
                <ol className="carousel-indicators">
                    <li data-target="#carouselId" data-slide-to={0} className="active" />
                    <li data-target="#carouselId" data-slide-to={1} />
                    <li data-target="#carouselId" data-slide-to={2} />
                </ol>
                <div className="carousel-inner" role="listbox">
                    <div className="carousel-item active">
                        <img data-src="holder.js/900x500/auto/#777:#555/text:First slide" alt="First slide" />
                    </div>
                    <div className="carousel-item">
                        <img data-src="holder.js/900x500/auto/#666:#444/text:Second slide" alt="Second slide" />
                    </div>
                    <div className="carousel-item">
                        <img data-src="holder.js/900x500/auto/#666:#444/text:Third slide" alt="Third slide" />
                    </div>
                </div>
                <a className="carousel-control-prev" href="#carouselId" role="button" data-slide="prev">
                    <span className="carousel-control-prev-icon" aria-hidden="true" />
                    <span className="sr-only">Previous</span>
                </a>
                <a className="carousel-control-next" href="#carouselId" role="button" data-slide="next">
                    <span className="carousel-control-next-icon" aria-hidden="true" />
                    <span className="sr-only">Next</span>
                </a>
            </div>

        </div>
    )
}
