import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { userSignupApi } from '../../redux/actions/GetUserAction';

export default function Signup() {

    const { } = useSelector(state => state.GetUserReducer)
    const dispatch = useDispatch();
    let [createUser, setCreateUser] = useState({

        taiKhoan: "string",
        matKhau: "string",
        email: "string",
        soDt: "string",
        maNhom: "GP01",
        maLoaiNguoiDung: "KhachHang",
        hoTen: "string"

    })

    console.log(createUser);

    const userSignup = (createUser) => {
        dispatch(userSignupApi(createUser))
    }

    const handleChange = (e) => {
        const { name, value } = e.target;

        const newCreateUser = {
            ...createUser,
            [name]: value
        }

        setCreateUser(newCreateUser);
    }

    const handleSignUp = (createUser) => {
        userSignup(createUser)
    }


    return (
        <div className="">
            <div className="signin" style={{ backgroundImage: `url('./background.jpg')` }}>
                <div className="signinWrapper">
                    <img className="singinHeader" src="./LoginImg/tixlogo.png" />
                    <form className="form-group" onSubmit={handleSignUp}>
                        <input type="text" className="form-control" name="taiKhoan" id onChange={handleChange} aria-describedby="helpId" placeholder="Username" />
                        <input type="text" className="form-control" name="matKhau" id onChange={handleChange} aria-describedby="helpId" placeholder="Password" />
                        <input type="text" className="form-control" name="hoTen" id onChange={handleChange} aria-describedby="helpId" placeholder="Full Name" />
                        <input type="text" className="form-control" name="email" id onChange={handleChange} aria-describedby="helpId" placeholder="Email" />
                        <input type="text" className="form-control" name="soDt" id onChange={handleChange} aria-describedby="helpId" placeholder="Phone Number" />
                        <p style={{ fontSize: "11px" }}>Đăng ký tài khoản để được nhiều ưu đãi, mua vé và bảo mật thông tin!</p>
                        <button className="btnSignin btn">SIGN UP</button>
                        <p>
                            Nếu bạn đã có tài khoản, hãy đăng nhập
                        <a href="/login"> tại đây</a>
                        </p>
                    </form>

                </div>
            </div>
        </div>
    )
}
