import axios from 'axios';
import { GET_MOVIE_API, GET_THEATER_API, GET_THEATER_INFO_API } from '../constants/GetMovieConstants';

export const getMovieListApi = () => {
    return dispatch => {
        let promise = axios({
            url: 'https://movie0706.cybersoft.edu.vn/api/QuanLyPhim/LayDanhSachPhim?maNhom=GP01',
            method: 'GET'
        });

        promise.then((res) => {
            dispatch({
                type: GET_MOVIE_API,
                movieList: res.data
            })
        });

        promise.catch((err) => {
            console.log(err.response.data);
        })
    }
}

export const getTheaterListApi = () => {
    return dispatch => {
        let promise = axios({
            url: 'https://movie0706.cybersoft.edu.vn/api/QuanLyRap/LayThongTinHeThongRap',
            method: 'GET'
        });

        promise.then((res) => {
            console.log(res.data);
            dispatch({
                type: GET_THEATER_API,
                theaterList: res.data
            })
        });
        promise.catch((err) => {
            console.log(err.response.data);
        })
    }
}

export const getTheaterInfoApi = (maHeThongRap) => {
    return dispatch => {
        let promise = axios({
            url: `https://movie0706.cybersoft.edu.vn/api/QuanLyRap/LayThongTinCumRapTheoHeThong?maHeThongRap=${BHStar}`,
            method: 'GET'
        });

        promise.then((res) => {
            console.log(res.data);
            dispatch({
                type: GET_THEATER_INFO_API,
                theaterList: res.data
            })
        });
        promise.catch((err) => {
            console.log(err.response.data);
        })
    }
}
