import { GET_MOVIE_API, GET_THEATER_API } from '../constants/GetMovieConstants';

const initialState = {
    movieList: [],
    theaterList: [],

}

export default (state = initialState, action) => {
    switch (action.type) {

        case GET_MOVIE_API:
            state.movieList = action.movieList;
            return { ...state }
            break;

        case GET_THEATER_API:
            state.theaterList = action.theaterList;
            return { ...state }
            break;

        default:
            return state
    }
}
